fn test_parse_mcrl2_spec(input: &str, snapshot_file: &str) {
    test_logger();

    match UntypedProcessSpecification::parse(input) {
        Ok(spec) => {
            check_snapshot(&spec, Path::new(snapshot_file)).expect("Could not read or write the tests/snapshot file");
        }
        Err(err) => panic!("{}", err),
    }
}

